                    level1Start = null;
                    previousLog = null;
                }
            }
            if (log.status === 'level1' && !level1Start) {
                level1Start = log;
            } else if (log.status === 'level2') {
                previousLog = log;
            }
            if (index === 1 && log.status === 'off_level1' && level1Start) {
                let duration = new Date(log.date).getTime() - new Date(level1Start.date).getTime();
                lvl1.push([poste.name, level1Start.date, log.date, duration]);
                level1Start = null;
            }
        });
    }
});

