msg.payload.push(["", "S1", "S2", "S3", "S4", "S5"])
msg.log.forEach(function (poste) {
    if (poste && poste.isEnable) {
        var name = poste.name
        var finalResult = [name, 0, 0, 0, 0, 0];
        var originDate = new Date();
        var originDateUtc = moment(originDate).add(1, 'hour')
        if (poste.logs) {
            poste.logs.forEach(log => {
                var todayConvert = moment(originDateUtc);
                var dateConvert = moment(log.date);
                node.warn({
                    "log.date: ": log.date,
                    "dateConvert: ": dateConvert
                })
                var date5 = todayConvert.clone().subtract(5, 'hour');
                var date4 = todayConvert.clone().subtract(4, 'hour');
                var date3 = todayConvert.clone().subtract(3, 'hour');
                var date2 = todayConvert.clone().subtract(2, 'hour');
                var date1 = todayConvert.clone().subtract(1, 'hour');
                if (log.status === 'level1') {
                    /*
                    node.warn({
                        "name": name,
                        "todayConvert": todayConvert,
                        "dateConvert:": dateConvert,
                        "date5": date5,
                        "date4": date4,
                        "date3": date3,
                        "date2": date2,
                        "date1": date1,
                    })
                    */
                    if (dateConvert.isBetween(date5, date4)) {
                        node.warn('d5')
                        finalResult[1]++
                    } else if (dateConvert.isBetween(date4, date3)) {
                        node.warn('d4')
                        finalResult[2]++
                    } else if (dateConvert.isBetween(date3, date2)) {
                        node.warn('d3')
                        finalResult[3]++
                    } else if (dateConvert.isBetween(date2, date1)) {
                        node.warn('d2')
                        finalResult[4]++
                    } else if (dateConvert.isBetween(date1, todayConvert)) {
                        node.warn('d1')
                        finalResult[5]++
                    }
                }
            });
        }
        msg.payload.push(finalResult)
    }
})
return msg

