let lvl1 = [["Name", "Start time", "End time", "duration"]];
let lvl2 = [["Name", "Start time", "End time", "duration"]];
msg.log.forEach(function (poste) {
    if (poste && poste.isEnable && poste.logs) {
        let previousLog = null;
        let level1Start = null;
        poste.logs.forEach(function (log, index) {
            if (previousLog) {
                if (log.status === 'off_level1' && !level1Start) {
                    let duration = new Date(log.date).getTime() - new Date(previousLog.date).getTime();

                    lvl1.push([poste.name, previousLog.date, log.date, duration]);
                    previousLog = null;
                } else if (log.status === 'off_level2') {
                    let duration = new Date(log.date).getTime() - new Date(previousLog.date).getTime();
                    lvl2.push([poste.name, previousLog.date, log.date, duration]);
                    previousLog = null;
                } else if (log.status === 'level2' && level1Start) {
                    let duration = new Date(log.date).getTime() - new Date(level1Start.date).getTime();
                    lvl2.push([poste.name, level1Start.date, log.date, duration]);
                    level1Start = null;
                    previousLog = null;
                }
            }
            if (log.status === 'level1' && !level1Start) {
                level1Start = log;
            } else if (log.status === 'level2') {
                previousLog = log;
            }
            if (index === 1 && log.status === 'off_level1' && level1Start) {
                let duration = new Date(log.date).getTime() - new Date(level1Start.date).getTime();
                lvl1.push([poste.name, level1Start.date, log.date, duration]);
                level1Start = null;
            }
        });
